type Shape = 
  | { kind: 'circle'; radius: number }
  | { kind: 'square'; side: number }
// 当新增一种 Shape 时，由于 getArea 中没有处理这种情况，
// 会抛类型错误，但不会影响运行时
//| { kind: 'new'; side: number }

function getArea(shape: Shape): number {
  switch (shape.kind) {
    case 'circle':
      return Math.PI * shape.radius ** 2
    case 'square':
      return shape.side ** 2
    default:
      const _never: never = shape
      return _never
  }
}
