// 获取当前浏览器的刷新频率
const frameDelta = getFrameTime()
// frameDelta * 2 是指以刷新频率一半的速率执行回调
function throttle(fn, time = frameDelta * 2) {
  let running = false
  return function (...args) {
    !running && setTimeout(() => {
      running = true
      fn.bind(this)(...args)
      running = false
    }, time)
  }
}

function debounce(fn, time = 100) {
  let tick = null
  return function (...args) {
    tick && window.clearTimeout(tick)
    tick = setTimeout(() => {
        fn.bind(this)(...args)
        tick = null
    })
  }
}
