.card {
  justify-content: flex-start;
  overflow: hidden;
  cursor: pointer;

  // 每逢三个元素，则执行动画，源代码和我们分析的动画的顺序相反，圆环是从长变短，不过不影响
  .node {
    border: solid 5px #F29338;
    border-radius: 50%;


    &:nth-child(3n) {
      width: 40px;
      flex-basis: 40px;
      background: #F8C798;
      animation: change-circle-width 2s ease alternate infinite;
    }
  }
}

@keyframes change-circle-width {
  from {
    width: 40px;
    flex-basis: 40px;
    background: #F8C798;
  }
  60% {
    width: 20px;
    flex-basis: 20px;
    background: transparent;
  }
  // 动画 60% - 100% 这段时间，属性没有变动，所以图案看起来像是静止的。
  to {
    width: 20px;
    flex-basis: 20px;
    background: transparent;
  }
}

.card {
  cursor: pointer;

  // 鼠标悬浮时显示第二排的小圆球
  &:hover {
    .node {
      &:nth-child(2n)::after {
        visibility: unset;
      }
    }
  }

  .node {
    background: #71A2DB;
    outline: solid 1px white;

    // 3n-1，3n+1 一起使用时等价于 3n 
    &:nth-child(3n-1),
    &:nth-last-child(3n+1) {
      background: #C2D7F0;
    }

    // 去除末行及每行末尾的伪元素
    &:nth-child(10n)::after,
    &:nth-last-child(-n+10)::after {
      display: none;
    }

    &::after {
      left: 75%;
      top: 75%;
      width: 50%;
      height: 50%;
      border-radius: 50%;
      background: white;
      animation: card-4-circle-move 1s linear alternate infinite;
    }
    &:nth-child(2n)::after {
      animation: card-4-circle-move-delay 1s linear alternate infinite;
      animation-delay: .3s;
      visibility: hidden;
    }
  }
}

@keyframes card-4-circle-move {
  from {
    left: 45%;
    top: 45%;
    opacity: 1;
    background: white;
  }
  to {
    left: 130%;
    top: 130%;
    opacity: 0;
    background: #F2C07D;
  }
}
@keyframes card-4-circle-move-delay {
  from {
    left: 45%;
    top: 45%;
    opacity: 1;
    background: #F2C07D;
    z-index: 2;
  }
  to {
    left: 130%;
    top: 130%;
    opacity: 0;
    background: white;
  }
}

.card {
  overflow: hidden;
  cursor: pointer;

  // 根据三角形的序号与10的模来确定旋转角度
  @for $i from 0 through 9 {
    .node:nth-child(10n - #{$i})::before {
      transform: rotate((-19 + $i) + unquote('deg'));
    }
  }

  // 上面那串函数编译出来就成了下面这一长串模样
  // .node:nth-child(10n)::before {
  //   transform: rotate(-19deg);
  // }
  // .node:nth-child(10n-1)::before {
  //   transform: rotate(-18deg);
  // }
  // .node:nth-child(10n-2)::before {
  //   transform: rotate(-17deg);
  // }
  // .node:nth-child(10n-3)::before {
  //   transform: rotate(-16deg);
  // }
  // .node:nth-child(10n-4)::before {
  //   transform: rotate(-15deg);
  // }
  // .node:nth-child(10n-5)::before {
  //   transform: rotate(-14deg);
  // }
  // .node:nth-child(10n-6)::before {
  //   transform: rotate(-13deg);
  // }
  // .node:nth-child(10n-7)::before {
  //   transform: rotate(-12deg);
  // }
  // .node:nth-child(10n-8)::before {
  //   transform: rotate(-11deg);
  // }
  // .node:nth-child(10n-9)::before {
  //   transform: rotate(-10deg);
  // }

  .node {
    background: #F5C1CB;
    filter: saturate(1.6);

    // 通过伪元素 Border 绘制的三角形
    &::before {
      left: 0;
      top: -8px;
      border: solid 10px transparent;
      border-bottom-color: #D2F3BF;
      z-index: 1;
    }

    // 使用 Box-Shadow 属性，将圆形复制了额外的三份
    &::after {
      left: -5px;
      top: -5px;
      width: 9px;
      height: 9px;
      border-radius: 50%;
      background: #FBF5C5;
      z-index: 0;
      box-shadow: 20px 0 #FBF5C5, 20px 20px #FBF5C5, 0 20px #FBF5C5;
    }
  }
}

.card {
  .node {
    background: #EE92A5;
    overflow: hidden;
    transition: .3s;
    cursor: pointer;

    // 鼠标在格子上悬浮时的放大效果
    &:hover {
      transform: scale(1.4);
    }

    // 十字线的构成
    &::before {
      left: 8px;
      top: 8px;
      width: 2px;
      height: 2px;
      background: white;
      z-index: 0;
      box-shadow: 0 2px white, 2px 0 white, -2px 0 white, 0 -2px white;
    }

    // 圆形的构成
    &::after {
      left: -8px;
      top: -8px;
      width: 15px;
      height: 15px;
      border-radius: 50%;
      background: #F8C798;
      z-index: 0;
      box-shadow: 20px 0 #F8C798, 20px 20px #F8C798, 0 20px #F8C798;
    }

    // CSS 伪随机给特定元素设置特殊色。在实践时，可以自己调整以下参数，以达到想要的效果。
    &:nth-child(2n)::after {
      background: #E03A5C;
    }
    &:nth-child(3n-1)::after,
    &:nth-child(3n)::after,
    &:nth-child(5n)::after,
    &:nth-child(6n)::after,
    &:nth-child(7n-3)::after {
      background: #F8C798
    }
  }
}

.card {
  cursor: pointer;

  // 横线与竖线并不是节点的 Border，而是背景色+遮罩形成的
  // 格子会根据动画在高度上变化
  .node {
    background: #A45963;
    border-radius: 90%;
    animation: card-1 .4s ease alternate infinite;

    // 格子动画延迟处理
    &:nth-child(2n) {
      animation-delay: .2s;
    }
    &:nth-child(3n) {
      animation-delay: .3s;
    }
    &:nth-child(4n) {
      animation-delay: .3s;
    }

    // 山的颜色处理
    &:nth-child(2n)::before {
      border-bottom-color: #F5CB6C;
    }
    &:nth-child(3n)::before {
      border-bottom-color: #F5856C;
    }
    &:nth-child(4n)::before,
    &:nth-child(5n)::before,
    &:nth-child(6n)::before,
    &:nth-child(7n)::before,
    &:nth-child(8n)::before,
    &:nth-child(9n)::before,
    &:nth-child(10n)::before {
      border-bottom-color: #D2F3BF;
    }

    // 山的构成
    &::before {
      left: 0;
      top: -5px;
      border: solid 10px transparent;
      border-bottom-color: #D2F3BF;
      z-index: 2;
    }

    // 白色遮罩
    &::after {
      left: 1px;
      top: 1px;
      width: 19px;
      height: 18px;
      background: white;
    }

    // 这是一个特殊处理，为了让白色遮罩长度减少1像素以显示每行格子的背景颜色的最后一列像素
    &:nth-child(10n)::after {
      width: 18px;
    }
  }
}

@keyframes card-1 {
  from {
    height: 19px;
  }
  to {
    height: 8px;
  }
}

.card {
  .node {
    background: #71A2DB;

    // 部分仙人掌添加动画
    &:nth-child(3n)::after,
    &:nth-child(3n+2)::after,
    &:nth-child(5n-3)::after,
    &:nth-child(6n-2)::after,
    &:nth-child(7n+1)::after {
      animation: card-7-grow .6s ease alternate infinite;
    }

    // 一部分仙人掌不需要添加动画
    &:nth-child(3n-1)::after,
    &:nth-child(3n)::after,
    &:nth-child(5n)::after,
    &:nth-child(6n)::after,
    &:nth-child(7n-3)::after {
      clip-path: circle(75% at 0 50%);
      animation: none;
    }

    // 这里使用的是背景色 + Box-Shadow 画线。也可以使用 Border + Box-Shadow 画线
    &::before {
      top: 1px;
      left: 0px;
      width: 100%;
      height: 1px;
      background: white;
      box-shadow: 0 2px white, 0 4px white, 0 6px white, 0 8px white, 0 10px white, 0 12px white, 0 14px white, 0 16px white, 0 18px white;
    }
    &::after {
      top: 0;
      left: 1px;
      width: 1px;
      height: 100%;
      background: white;
      box-shadow: 2px 0 white, 4px 0 white, 6px 0 white, 8px 0 white, 10px 0 white, 12px 0 white, 14px 0 white, 16px 0 white, 18px 0 white;
      transition: .6s;
    }
  }

  // 鼠标悬浮时显示所有的线条（为了使 Clip-Path 有过渡效果，这里不能直接去掉属性，而是要换一个较大的值）
  &:hover {
    .node {
      &::after {
        animation: none;
        clip-path: circle(150% at 0% 50%);
      }
    }
  }
}

@keyframes card-7-grow {
  from {
    clip-path: circle(50% at 0 50%);
  }
  50% {
    clip-path: circle(50% at 0 50%);
  }
  to {
    clip-path: circle(150% at 0 50%);
  }
}

.card:nth-child(8) {
  .node {
    border: solid 8px #71A2DB;
    border-top: 0;
    border-left: 0;
    background: #71A2DB;
    clip-path: polygon(50% 0, 100% 50%, 50% 100%, 0% 50%);
    transition: .3s;
    cursor: pointer;

    // 给一部分格子去掉 Border
    &:nth-child(3n-1),
    &:nth-child(3n),
    &:nth-child(5n),
    &:nth-child(6n),
    &:nth-child(7n-3) {
      border: none;
      clip-path: circle(50%);

      &:hover {
        clip-path: circle(30%);
      }
    }

    // 将一部分格子裁剪为菱形区域。Clip-Path 四个值对应菱形四个顶点位置。
    &:nth-child(2n),
    &:nth-child(3n) {
      border: solid 8px #CCDDF2;
      clip-path: polygon(50% 0, 100% 50%, 50% 100%, 0% 50%);
    }

    &::before {
      top: 1px;
      left: 0px;
      width: 100%;
      height: 1px;
      background: white;
      box-shadow: 0 2px white, 0 4px white, 0 6px white, 0 8px white, 0 10px white, 0 12px white, 0 14px white, 0 16px white, 0 18px white;
    }
    &::after {
      top: 0;
      left: 1px;
      width: 1px;
      height: 100%;
      background: white;
      box-shadow: 2px 0 white, 4px 0 white, 6px 0 white, 8px 0 white, 10px 0 white, 12px 0 white, 14px 0 white, 16px 0 white, 18px 0 white;
    }
  }
}

// 可以看到父元素用到了 filter: contrast 滤镜
.card {
  position: relative;
  box-sizing: border-box;
  flex-direction: column;
  justify-content: flex-end;
  align-items: center;
  border: solid .5em transparent;
  border-image: 8 repeating-linear-gradient(-45deg, #F5E66C 0, #F5E66C .5em, transparent 0, transparent 1em, #DA60D2 0, #DA60D2 1.5em, transparent 0, transparent 2em);
  background: white;
  cursor: pointer;
  filter: contrast(10);

  // 给每个格子分别设定背景颜色和动画延迟
  $background:(#DA60D2, #E7667E, #E7667E, #F5866C, #F5866C, #F5E66C);
  @for $i from 1 through 6 {
    .node:nth-child(#{$i}) {
      width: (80-(10 * ($i - 1)))+unquote('px');
      animation: card-6 .8s ease-in (0.1*$i)+unquote('s') alternate infinite, card-6-margin .8s ease-in alternate infinite;
      background: nth($background, $i);
    }
  }

  // 格子使用了 blur 滤镜
  .node {
    flex-basis: 30px;
    margin-top: -15px;
    width: 30px;
    height: 50px;
    filter: blur(5px);
  }

  // 鼠标悬浮时暂停动画，因为子元素的融化效果，所以需要把字体调粗一些
  &:hover {
    &::before {
      content: "Paused";
      position: absolute;
      left: 5px;
      top: 5px;
      font-weight: bolder;
    }
    .node {
      animation-play-state: paused;
    }
  }

  @keyframes card-6 {
    from {
      border-radius: 50%;
    }
    to {
      width: 80px;
      border-radius: 0;
    }
  }
  @keyframes card-6-margin {
    from {
      margin-top: -13px;
    }
    to {
      margin-top: 0px;
    }
  }
}

.card {
  background: linear-gradient(45deg, #F5CB6C 0%,#F5CB6C 20%,#F5856C 20%, #F5856C 45%,#F5CB6C 45%,#F5CB6C 70%,#F5856C 70%, #F5856C 95%,#F5CB6C 95%,#F5CB6C 100%);
  background-size:30px 30px;
  background-position:0 0;
  animation: card-5 1s infinite linear;
  cursor: pointer;

  .node {
    // 使用 Background-Clip 的伪元素
    &::before {
      content: "Lionad";
      left: -1.5em;
      top: -.7em;
      font-size: 50px;
      font-family: didot;
      font-weight: bolder;
      color: transparent;
      background: linear-gradient(180deg, #F5CB6C, #F5856C);
      background-size: 1px 2px;
      background-clip: text;
      -webkit-background-clip: text;
      z-index: 2;
    }

    // 生产 Text-Shadow 的伪元素
    &::after {
      content: "Lionad";
      left: -1.5em;
      top: -.7em;
      font-size: 50px;
      font-family: didot;
      font-weight: bolder;
      color: transparent;
      text-shadow: 4px 4px 0px #F5856C;
      box-shadow: 0 5px 0px #F5CB6C, 0 12px 0px #F5856C;
    }
  }
  @keyframes card-5 {
    0%{
      background-position: 0 0;
    }
    100%{
      background-position: 30px 0;
    }
  }
}

// 右图的样式代码
.card {

  // 这里使用了三层背景渐变，两层圆锥渐变和一层辐射渐变
  background-image:
    repeating-conic-gradient(red 50%, #E1F5C4 60%),
    repeating-conic-gradient(red 50%, #E1F5C4 60%),
    radial-gradient(
      gold 0%, gold 35%,
      red 35%, red 40%,
      orangered 40%, orangered 50%,
      gold 50%, gold 60%,
      yellowgreen 60%, yellowgreen 70%,
      skyblue 70%, skyblue 80%,
      steelblue 80%, steelblue 90%,
      violet 90%
    );

  // 对每一层被渐变分别设置混合模式
  background-blend-mode: 
    lighten, 
    overlay,
    lighten; 

  // 对每一层被渐变分别设置背景大小（40px 是因为正好能被 200px 的盒子整除）
  background-size: 
    40px 40px,
    6em 6em,
    8em 8em;
  background-position: 50% 50%;
  transition: .45s ease-in;
  cursor: pointer;

  // 鼠标悬浮时，变换渐变大小
  &:hover {
    background-size: 
      40px 40px,
      4em 4em,
      12em 12em;
  }
}

