        transform: rotate((90 + $i * $part-degree) + unquote('deg'));

        // ä¸åŒçš„ç›’å­çš„ :hover çŠ¶æ€ï¼Œä¼šæ”¹å˜å…¶å…„å¼Ÿ .head ç±»ç›’å­é‡Œçš„ä¸€äº›ä¸œè¥¿ï¼Œè¿™é‡Œæ¶‰åŠåˆ°çœ¼ç›æ˜¯å¦‚ä½•åˆ¶ä½œçš„ï¼Œç¨åä¼šè¯´ã€‚
        &:hover ~ .head {
            $ty: sin($angle) * $face-width / 50;
