function cloneDeep(obj) {
    const isObject = obj => typeof obj === 'object'
    if (!isObject(obj)) return obj
    const ret = Object.create(obj.__proto__ || null)
    for (let key in obj) {
        if (obj.hasOwnProperty(key)) {
            ret[key] = cloneDeep(obj[key])
        }
    }
    return ret
}

