let id = 0
const nodeList = []
export default {
  // Êåá‰ª§ÂàùÂßãÂåñÊó∂
  bind(el, binding, vnode) {
    // ÊääÂÖÉÁ¥†Êé®ÂÖ• nodeList
    nodeList.push(el)
    // ÁªôÂÖÉÁ¥†ÊåÇ‰∏äÊ†áËØÜÁ¨¶ÂíåÂõûË∞ÉÂáΩÊï∞
    el[ctx] = {
      // ‰øùËØÅÊ†áËØÜÁ¨¶ÂÖ®Â±ÄÂîØ‰∏Ä
      id: id++,
      // Áî®Êù•Â§ÑÁêÜ document Êé•Êî∂Âà∞ÁöÑÁÇπÂáª‰∫ã‰ª∂ÔºåÂà§Êñ≠ÁÇπÂáª‰∫ã‰ª∂‰∏≠ÁÇπÂáªÁöÑÂÖÉÁ¥†ÊòØÂê¶ÊòØËá™Ë∫´ÔºàÂç≥ elÔºâ
      documentHandler: createDocumentHandler(el, binding, vnode),
      // ÂõûË∞ÉÂáΩÊï∞ÁöÑÂêçÁß∞
      methodName: binding.expression
    }
  },
  // Êåá‰ª§Êî∂Âà∞ËäÇÁÇπÊõ¥Êñ∞‰∫ã‰ª∂ÔºåÈúÄË¶ÅÈáçÊñ∞ÁªëÂÆöÔºå
  // ‰ª•ÈáäÊîæÈó≠ÂåÖ‰∏≠ÁöÑÊóßÁöÑËäÇÁÇπ‰ø°ÊÅØ‰ª•Èò≤ÂÜÖÂ≠òÊ≥ÑÊºè
  update(el, binding, vnode) {
    el[ctx].documentHandler = createDocumentHandler(el, binding, vnode)
    el[ctx].methodName = binding.expression
  },
  // ÈîÄÊØÅÁªëÂÆöÊó∂ÔºåÈÄöËøá ID ÊâæÂà∞Âπ∂Âà†Èô§ nodeList ‰∏≠ÂØπÂ∫îÂÖÉÁ¥†
  unbind(el) {
    let len = nodeList.length
    for (let i = 0 i < len i++) {
      if (nodeList[i][ctx].id === el[ctx].id) {
        nodeList.splice(i, 1)
        break
      }
    }
    delete el[ctx]
  }
}

let startClick = null;
document.addEventListener("mousedown", (e) => (startClick = e));
document.addEventListener("mouseup", (e) => {
  nodeList.forEach((node) => node[ctx].documentHandler(e, startClick));
});

function createDocumentHandler(el, binding, vnode) {
  return function (mouseup = {}, mousedown = {}) {
    if (
      !vnode ||
      !vnode.context ||
      !mouseup.target ||
      !mousedown.target ||
      // ‰ΩøÁî® contains API Âà§Êñ≠ÂÖÉÁ¥†ÊòØÂê¶ÂåÖÂê´ÁÇπÂáªÂÖÉÁ¥†Ôºå
      // ‰πüÂ∞±ÊòØËØ¥Â¶ÇÊûúÁÇπÂáª‰∫ã‰ª∂‰∏≠Ëß¶ÂèëÁöÑÂÖÉÁ¥†Êó¢‰∏çÊòØÊåá‰ª§ÊåÇËΩΩÂÖÉÁ¥†ÁöÑÂ≠êÂÖÉÁ¥†ÊàñÂÖ∂Êú¨Ë∫´Ôºå
      // ÈÇ£Â∞±ÁÆó‚ÄúÁÇπÂáªÂ§ñÈÉ®ÂÖÉÁ¥†‚ÄùÔºåÈúÄË¶ÅËß¶ÂèëÂõûË∞É‰∫Ü
      el.contains(mouseup.target) ||
      el.contains(mousedown.target) ||
      el === mouseup.target
    )
      return;

    // Ëß¶ÂèëÂõûË∞ÉÂáΩÊï∞
    if (
      binding.expression &&
      el[ctx].methodName &&
      vnode.context[el[ctx].methodName]
    ) {
      vnode.context[el[ctx].methodName]();
    }
  };
}

