/**
 * @example
 * camelCase('a-b__cc') // -> "aBCc"
 */
function camelCase(s) {
    const shouldConvert = !(s.indexOf('-') === -1 && s.indexOf('_') === -1)
    return shouldConvert
        ? s.replace(/[-_]+([^-_])/g, (...args) => args[1].toUpperCase())
        : s
}

/**
 * @param {Function} callback The function to combine each word.
 * @returns {Function} Returns the new compounder function.
 */
function createCompounder(callback) {
    return function(string) {
        // 注意，这里会去除一些特殊字符
        return arrayReduce(words(deburr(string).replace(reApos, '')), callback, '')
    }
}

// 将不是第一个的词组转换为大写首字母形式
var camelCase = createCompounder(function(result, word, index) {
    word = word.toLowerCase()
    return result + (index ? capitalize(word) : word)
})

// 词组之间用中隔线连接
var kebabCase = createCompounder(function(result, word, index) {
    return result + (index ? '-' : '') + word.toLowerCase()
})

// 词组之间用下划线连接
var snakeCase = createCompounder(function(result, word, index) {
    return result + (index ? '_' : '') + word.toLowerCase()
})

/**
 * @example
 * _.truncate('hi-diddly-ho there, neighborino', {
 *   length: 24,
 *   separator: /,? +/,
 *   omission: ' [...]'
 * })
 * // -> 'hi-diddly-ho there [...]'
 */

function truncate(string, options) {
    let length = +options.length || 30
    let omission = options.opmission || '...'
    let separator = options.separator

    /* 对包含 Unicode 字符的字符串特殊处理*/
    let strSymbols = null
    let strLength = string.length
    if (hasUnicode(string)) {
        strSymbols = stringToArray(string)
        strLength = strSymbols.length
    }

    /* 极值情况 */
    let end = length - omission.length
    if (length > strLength) {
        return string
    } else if (end < 1) {
        return omission
    }

    /* 裁剪字符串并处理分隔符 */
    let result = strSymbols
        ? strSymbols.slice(0, end).join('')
        : string.slice(0, end)
    if (!separator) {
        return result + omission
    } else {
        // 如果有分隔符，则将结果裁剪到最后一个分隔符的位置
        if (isRegExp) {
            end = result.length
            if (!separator.global) {
                separator = RegExp(separator.source, /\w*$/.exec(separator) + 'g')
            }
            let match = null
            let newEnd = null
            while ((match = separator.exec(result))) {
                newEnd = match.index
            }
            if (newEnd) {
                result = result.slice(0, newEnd)
            }
        } else if (string.indexOf(separator, end) !== end) {
            const idx = result.lastIndexOf(separator)
            if (idx !== -1) {
                result = result.slice(0, idx)
            }
        }
    }

    return result + omission
}

function truncate (s, len = 30, omission = '...') {
    return s.length > len
        ? s.slice(0, len) + omission
        : String(s)
}

// '𝌆'.padStart(3) // -> " 𝌆"

function padStart(string, length, chars) {
    string = toString(string)
    length = toInteger(length)

    var strLength = length ? stringSize(string) : 0
    return length && strLength < length ? createPadding(length - strLength, chars) + string : string
}

function padEnd(string, length, chars) {
    string = toString(string)
    length = toInteger(length)

    var strLength = length ? stringSize(string) : 0
    return length && strLength < length ? string + createPadding(length - strLength, chars) : string
}

function repeat(s, n) {
    // // return s * n 
    // # 不好意思，走错片场了

    if (!s || n < 1 || n === Infinity) {
        throw new Error('Args error')
    }

    let res = ''
    while (n) {
        if (n % 2) {
            res += s
        }
        s += s
        if (n === 1) break
        n = n >> 1
    }
    return res
}

function repeat(s, n) {
    if (n === 1) {
        return s
    }
    let res = repeat(s, n >> 1)
    res += res
    if (n % 2) {
        res += s
    }
    return res
}

var reEscapedHtml = /&(?:amp|lt|gt|quot|#39);/g,
    reUnescapedHtml = /[&<>"']/g,
    reHasEscapedHtml = RegExp(reEscapedHtml.source),
    reHasUnescapedHtml = RegExp(reUnescapedHtml.source)

var htmlUnescapes = {
    '&amp;': '&',
    '&lt;': '<',
    '&gt;': '>',
    '&quot;': '"',
    '&#39;': "'"
}

function unescape(string) {
    string = toString(string)
    return string && reHasEscapedHtml.test(string) 
        ? string.replace(reEscapedHtml, c => htmlUnescapes[c]) 
        : string
}

