function flushCallbacks() {
  /* ... */
}
// timerFunc Áî®Êù•Â≠òÂÇ® hackedQueueMicroTask API
let timerFunc;
// Â¶ÇÊûúËøêË°åÁéØÂ¢ÉÊîØÊåÅ PromiseÔºåÂàô‰ΩøÁî® Promise.then
if (typeof Promise !== "undefined" && isNative(Promise)) {
