function observe(value, asRootData) {
  ob = new Observer(value);
  return ob;
}

class Observer {
  constructor(value) {
    this.value = value;
    // 初始化依赖容器
    this.dep = new Dep();
    this.vmCount = 0;
    // 将侦测实例实例挂载到对象上方便访问
    def(value, "__ob__", this);
    // 如果是数组，则给它挂载带有依赖收集及触发依赖更新增强的原型函数，并遍历元素设置响应式属性；
    // 如果是对象，则遍历对象的每一个值并递归遍历子对象，设置响应式属性
    if (Array.isArray(value)) {
      if (hasProto) {
        protoAugment(value, arrayMethods);
      } else {
        copyAugment(value, arrayMethods, arrayKeys);
      }
      this.observeArray(value);
    } else {
      this.walk(value);
    }
  }

  walk(obj) {
    const keys = Object.keys(obj);
    for (let i = 0; i < keys.length; i++) {
      defineReactive(obj, keys[i]);
    }
  }

  observeArray(items) {
    for (let i = 0, l = items.length; i < l; i++) {
      observe(items[i]);
    }
  }
}

// 需要增强的方法
const methodsToPatch = [
  "push",
  "pop",
  "shift",
  "unshift",
  "splice",
  "sort",
  "reverse",
];

methodsToPatch.forEach(function (method) {
  // cache original method
  const original = arrayProto[method];
  def(arrayMethods, method, function mutator(...args) {
    // 先执行方法获取结果，而不是先通知依赖更新，这个顺序要注意
    const result = original.apply(this, args);
    // observer 在设置响应式属性时就被挂载在了对象的 \_\_ob\_\_ 属性中
    const ob = this.__ob__;
    // inserted 用来保存数组中新增的元素，这些新增元素也需要遍历并设置响应式更新
    let inserted;
    switch (method) {
      case "push":
      case "unshift":
        inserted = args;
        break;
      case "splice":
        inserted = args.slice(2);
        break;
    }
    if (inserted) ob.observeArray(inserted);
    // 通知依赖更新
    ob.dep.notify();
    return result;
  });
});

function defineReactive(obj, key, val, customSetter, shallow) {
  // 每个属性都保持了一个依赖容器，
  // 所有依赖都会存放在这个依赖容器实例中
  const dep = new Dep();

  // 不给属性描述符不可变的对象设置 setter/getter
  const property = Object.getOwnPropertyDescriptor(obj, key);
  if (property && property.configurable === false) {
    return;
  }

  // 保存属性原有的 setter/getter，不做破坏
  // cater for pre-defined getter/setters
  const getter = property && property.get;
  const setter = property && property.set;
  if ((!getter || setter) && arguments.length === 2) {
    val = obj[key];
  }

  // 侦测值的变化
  let childOb = !shallow && observe(val);

  Object.defineProperty(obj, key, {
    enumerable: true,
    configurable: true,
    get: function reactiveGetter() {
      const value = getter ? getter.call(obj) : val;
      // 收集依赖
      if (Dep.target) {
        dep.depend();
        if (childOb) {
          childOb.dep.depend();
          if (Array.isArray(value)) {
            dependArray(value);
          }
        }
      }
      return value;
    },
    set: function reactiveSetter(newVal) {
      const value = getter ? getter.call(obj) : val;
      /* eslint-disable no-self-compare */
      if (newVal === value || (newVal !== newVal && value !== value)) {
        return;
      }
      // 非生产环境：自定义 setter 副作用
      if (process.env.NODE_ENV !== "production" && customSetter) {
        customSetter();
      }
      // #7981: for accessor properties without setter
      if (getter && !setter) return;
      // 使用原有的 setter 更新值或直接更新值
      if (setter) {
        setter.call(obj, newVal);
      } else {
        val = newVal;
      }
      // 侦测子对象的变化
      childOb = !shallow && observe(newVal);
      // 通知依赖更新
      dep.notify();
    },
  });
}

class Dep {
  constructor() {
    this.id = uid++;
    this.subs = [];
  }
  // 添加依赖
  addSub(sub) {
    this.subs.push(sub);
  }
  // 删除依赖
  removeSub(sub) {
    remove(this.subs, sub);
  }
  // 收集依赖
  depend() {
    if (Dep.target) {
      Dep.target.addDep(this);
    }
  }
  // 通知依赖更新
  notify() {
    // stabilize the subscriber list first
    const subs = this.subs.slice();
    for (let i = 0, l = subs.length; i < l; i++) {
      subs[i].update();
    }
  }
}

// The current target watcher being evaluated.
// This is globally unique because only one watcher
// can be evaluated at a time.
Dep.target = null;
const targetStack = [];
function pushTarget(target) {
  targetStack.push(target);
  Dep.target = target;
}
function popTarget() {
  targetStack.pop();
  Dep.target = targetStack[targetStack.length - 1];
}

class Watcher {
  // 依赖实例通过 get 方法获取对象属性的值，以触发属性的 getter 以重收集依赖
  // 这里的“重收集”，意味着将当前依赖重新添加到不同对象的属性的依赖容器中
  // 所以在这里需要使用 pushTarget、popTarget 维护全局的当前依赖
  get() {
    pushTarget(this);
    let value;
    // vm 即该依赖对应的 Vue 实例，这个后续会介绍
    const vm = this.vm;
    // 触发 getter 更新
    try {
      value = this.getter.call(vm, vm);
    } catch (e) {
      if (this.user) {
        handleError(e, vm, `getter for watcher "${this.expression}"`);
      } else {
        throw e;
      }
    } finally {
      // 如果依赖设置了 deep 选项，那会触发对应 value 的所有键的依赖更新
      // traverse 即递归读取对象的每一个元素
      if (this.deep) {
        traverse(value);
      }
      popTarget();
      // 因为每次 get 之后都重收集了依赖，
      // 这里会把没有用的依赖给清除掉（调用依赖容器的 removeSub 移出依赖）
      this.cleanupDeps();
    }
    return value;
  }
}

