function cloneVNode(vnode) {
  const clonedChild = (vnode.children || []).map((vnode) => cloneVNode(vnode));
  const cloned = createElement(vnode.tag, vnode.data, clonedChild);
  const copyProps = [
    "text",
    "isComment",
    "isStatic",
    "key",
    "elm",
    "context",
    "ns",
    "componentOptions",
  ];
  copyProps.map((prop) => (cloned[prop] = vnode[prop]));
  return cloned;
}

const splits = codes.split(/\n/);
const tabs = splits.map((x) => x.search(/[^\s]/));
const minSpace = Math.min(...tabs);
const reMinSpace = new RegExp(`\\s{${minSpace}}`);
codes = splits.map((x) => x.replace(reMinSpace, "")).join("\n");

var splitRE = /\r?\n/g;
var emptyRE = /^\s*$/;
var needFixRE = /^(\r?\n)*[\t\s]/;

module.exports = function deindent(str) {
  // å¦‚æžœç¬¬ä¸€è¡Œå‰æ²¡æœ‰ç©ºæ ¼ï¼Œç›´æŽ¥é€€å‡º
  if (!needFixRE.test(str)) {
    return str;
  }
  // å°†ä»£ç æ‹†åˆ†æˆè¡Œ
  var lines = str.split(splitRE);

  /* è¿™é‡Œåªå¾ªçŽ¯äº†ä¸€æ¬¡å°±æ‰¾åˆ°äº†æœ€å°ç©ºæ ¼æ•°é‡ï¼Œå¹¶ä¸”åšäº†ä¼˜åŒ– */
  var min = Infinity;
  // type ç”¨æ¥è®°å½•ç¼©è¿›æ˜¯ tab è¿˜æ˜¯ç©ºæ ¼
  var type, cur, c;
  for (var i = 0; i < lines.length; i++) {
    var line = lines[i];
    if (!emptyRE.test(line)) {
      if (!type) {
        c = line.charAt(0);
        if (c === " " || c === "\t") {
          type = c;
          cur = count(line, type);
          if (cur < min) {
            min = cur;
          }
        } else {
          return str;
        }
      } else {
        cur = count(line, type);
        if (cur < min) {
          min = cur;
        }
      }
    }
  }
  // ç”¨ String.slice åˆ‡é™¤å‰ç½®ç©ºæ ¼ï¼Œæ€§èƒ½æœ€å¥½
  return lines
    .map(function (line) {
      return line.slice(min);
    })
    .join("\n");
};

function count(line, type) {
  var i = 0;
  while (line.charAt(i) === type) {
    i++;
  }
  return i;
}
